import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line
} from 'recharts';

const Analytics = () => {
  const { user } = useAuth();
  const [students, setStudents] = useState([]);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      return (
        <div className="container mx-auto p-6">
          <div className="text-center">
            <h1 className="text-3xl font-bold text-red-600 mb-4">Access Denied</h1>
            <p className="text-gray-600">You need admin privileges to access this page.</p>
          </div>
        </div>
      );
    }

    const fetchAnalyticsData = async () => {
      try {
        setLoading(true);
        
        // Fetch all students from database
        const studentsResponse = await axios.get('http://localhost:8002/api/students/');
        const studentsData = studentsResponse.data;
        setStudents(studentsData);

        // Fetch all predictions from localStorage
        const allPredictions = [];
        for (let i = 0; i < localStorage.length; i++) {
          const key = localStorage.key(i);
          if (key && key.startsWith('predictions_')) {
            const predictions = JSON.parse(localStorage.getItem(key));
            if (predictions.length > 0) {
              const userEmail = key.replace('predictions_', '');
              allPredictions.push({
                studentEmail: userEmail,
                prediction: predictions[0]
              });
            }
          }
        }
        setPredictions(allPredictions);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching analytics data:', error);
        setError('Failed to load analytics data');
        setLoading(false);
      }
    };

    fetchAnalyticsData();
  }, [user]);

  // Calculate analytics metrics
  const calculateMetrics = () => {
    const totalStudents = students.length;
    const studentsWithPredictions = students.filter(student => 
      predictions.some(p => p.studentEmail === student.email)
    );
    
    const riskLevels = {
      low: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.risk_score < 0.3;
      }).length,
      medium: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.risk_score >= 0.3 && prediction.prediction.risk_score < 0.7;
      }).length,
      high: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.risk_score >= 0.7;
      }).length
    };

    const performanceLevels = {
      excellent: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.predicted_performance === 'Excellent';
      }).length,
      good: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.predicted_performance === 'High';
      }).length,
      average: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.predicted_performance === 'Medium';
      }).length,
      poor: studentsWithPredictions.filter(s => {
        const prediction = predictions.find(p => p.studentEmail === s.email);
        return prediction && prediction.prediction.predicted_performance === 'Low';
      }).length
    };

    const averageGpa = students.reduce((sum, s) => sum + (s.previous_gpa || 0), 0) / totalStudents || 0;
    const averageAttendance = students.reduce((sum, s) => sum + (s.attendance_percentage || 0), 0) / totalStudents || 0;
    const averageStudyHours = students.reduce((sum, s) => sum + (s.study_hours || 0), 0) / totalStudents || 0;

    return {
      totalStudents,
      studentsWithPredictions,
      riskLevels,
      performanceLevels,
      averageGpa,
      averageAttendance,
      averageStudyHours
    };
  };

  const metrics = calculateMetrics();

  // Prepare chart data
  const riskDistributionData = [
    { name: 'Low Risk', value: metrics.riskLevels.low, color: '#10B981' },
    { name: 'Medium Risk', value: metrics.riskLevels.medium, color: '#F59E0B' },
    { name: 'High Risk', value: metrics.riskLevels.high, color: '#EF4444' }
  ];

  const performanceDistributionData = [
    { name: 'Excellent', value: metrics.performanceLevels.excellent, color: '#10B981' },
    { name: 'Good', value: metrics.performanceLevels.good, color: '#3B82F6' },
    { name: 'Average', value: metrics.performanceLevels.average, color: '#F59E0B' },
    { name: 'Poor', value: metrics.performanceLevels.poor, color: '#EF4444' }
  ];

  const gpaDistributionData = students.map(student => ({
    name: student.name,
    gpa: student.previous_gpa || 0,
    attendance: student.attendance_percentage || 0,
    studyHours: student.study_hours || 0
  }));

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FF0000'];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          <p className="mt-4 text-gray-600">Loading analytics data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-red-600 mb-4">Error</h1>
          <p className="text-gray-600">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-7xl mx-auto px-4">
        
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-gray-800">Analytics Dashboard</h1>
          <p className="text-gray-500">Comprehensive student performance insights</p>
        </div>

        {/* Key Metrics */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Total Students</h3>
            <p className="text-3xl font-bold text-blue-600">{metrics.totalStudents}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">With Predictions</h3>
            <p className="text-3xl font-bold text-green-600">{metrics.studentsWithPredictions}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Average GPA</h3>
            <p className="text-3xl font-bold text-purple-600">{metrics.averageGpa.toFixed(2)}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Average Attendance</h3>
            <p className="text-3xl font-bold text-orange-600">{metrics.averageAttendance.toFixed(1)}%</p>
          </div>
        </div>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          
          {/* Risk Distribution Pie Chart */}
          <div className="bg-white rounded-xl shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Risk Distribution</h2>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={riskDistributionData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, value }) => `${name}: ${value}`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {riskDistributionData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Performance Distribution Pie Chart */}
          <div className="bg-white rounded-xl shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Performance Distribution</h2>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={performanceDistributionData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, value }) => `${name}: ${value}`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {performanceDistributionData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* GPA vs Study Hours Scatter Plot */}
        <div className="bg-white rounded-xl shadow p-6">
          <h2 className="text-xl font-semibold mb-4">GPA vs Study Hours Correlation</h2>
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={gpaDistributionData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="gpa" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Detailed Students Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-800">Student Details</h2>
            <p className="text-sm text-gray-500 mt-1">Complete overview of all student performance metrics</p>
          </div>
          
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Major</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">GPA</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Attendance</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Study Hours</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Risk Level</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Performance</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {students.map((student, index) => {
                  const prediction = predictions.find(p => p.studentEmail === student.email);
                  const riskScore = prediction?.prediction?.risk_score || 0;
                  const performanceLevel = prediction?.prediction?.predicted_performance || 'No Data';
                  
                  const getRiskColor = (score) => {
                    if (score >= 0.7) return 'text-red-600 bg-red-100';
                    if (score >= 0.3) return 'text-yellow-600 bg-yellow-100';
                    return 'text-green-600 bg-green-100';
                  };
                  
                  const getPerformanceColor = (level) => {
                    switch (level?.toLowerCase()) {
                      case 'excellent': return 'text-green-600 bg-green-100';
                      case 'good': return 'text-blue-600 bg-blue-100';
                      case 'average': return 'text-yellow-600 bg-yellow-100';
                      case 'low': return 'text-orange-600 bg-orange-100';
                      default: return 'text-gray-600 bg-gray-100';
                    }
                  };
                  
                  return (
                    <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="h-10 w-10 flex-shrink-0">
                            <div className="h-10 w-10 rounded-full bg-blue-500 flex items-center justify-center">
                              <span className="text-white font-medium text-sm">
                                {student.name.charAt(0).toUpperCase()}
                              </span>
                            </div>
                          </div>
                          <div className="ml-4">
                            <div className="text-sm font-medium text-gray-900">{student.name}</div>
                            <div className="text-sm text-gray-500">{student.major || 'Not specified'}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.email}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.major || 'Not specified'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.previous_gpa?.toFixed(2) || 'N/A'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.attendance_percentage || 0}%
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.study_hours || 0}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getRiskColor(riskScore)}`}>
                          {riskScore >= 0.7 ? 'High' : riskScore >= 0.3 ? 'Medium' : 'Low'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPerformanceColor(performanceLevel)}`}>
                          {performanceLevel}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          onClick={() => navigate(`/students/${student.id || student._id}`)}
                          className="text-blue-600 hover:text-blue-900 mr-3"
                        >
                          View Details
                        </button>
                        <button
                          onClick={() => alert(`Send intervention alert to ${student.email}`)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Send Alert
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
