import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { useAuth } from '../contexts/AuthContext';

const StudentProfile = () => {
  const { user } = useAuth();
  const [student, setStudent] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!user) {
      setError('Please login to view your profile');
      setLoading(false);
      return;
    }

    const studentId = user.id;
    
    const loadStudentData = async () => {
      console.log('ðŸ” Loading student data for user:', user);
      console.log('ðŸ” User ID:', user.id);
      
      // First check localStorage for academic data
      const storedAcademicData = localStorage.getItem(`academic_${user.email.toLowerCase()}`);
      console.log('ðŸ” Stored academic data found:', !!storedAcademicData);
      console.log('ðŸ” Stored data content:', storedAcademicData);
      
      if (storedAcademicData) {
        try {
          const academicData = JSON.parse(storedAcademicData);
          console.log('ðŸ” Parsed academic data:', academicData);
          
          const studentData = {
            id: studentId,
            name: user.name,
            email: user.email,
            ...academicData,
            socio_academic_factors: '{"family_income": "medium", "parent_education": "college"}'
          };
          
          console.log('ðŸ” Final student data:', studentData);
          setStudent(studentData);
          setLoading(false);
          return;
        } catch (parseError) {
          console.error('ðŸ” Error parsing stored data:', parseError);
        }
      }

      // If no localStorage data, try backend
      console.log('ðŸ” No localStorage data, trying backend...');
      try {
        const response = await axios.get(`http://localhost:8000/api/students/${studentId}`);
        console.log('ðŸ” Backend response:', response.data);
        setStudent(response.data);
      } catch (backendError) {
        console.log('ðŸ” Backend not available and no localStorage data:', backendError);
        // Show message to add academic details
        setError('Please add your academic details first using the "Add Academic Details" button in the navbar');
      }
      
      setLoading(false);
    };

    loadStudentData();
    
    // Load predictions
    const loadPredictions = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/predictions/history/${studentId}`);
        setPredictions(response.data);
      } catch (backendError) {
        console.log('Backend predictions not available, loading from localStorage:', backendError);
        
        // Fallback to localStorage
        const storedPredictions = localStorage.getItem(`predictions_${user.email.toLowerCase()}`);
        if (storedPredictions) {
          setPredictions(JSON.parse(storedPredictions));
        }
      }
    };

    loadPredictions();
  }, [user]);

  const generatePrediction = async () => {
    if (!student) return;

    // Generate prediction based on actual student data
    const recommendations = [
      student.attendance_percentage < 80 ? "Improve class attendance" : null,
      student.study_hours < 20 ? "Increase weekly study hours" : null,
      student.internal_marks < 75 ? "Focus on internal assessments" : null,
      student.assignment_scores < 75 ? "Work on assignment quality" : null,
      student.lab_performance < 75 ? "Improve lab performance" : null,
      student.participation_metrics < 70 ? "Increase class participation" : null
    ].filter(Boolean);

    // Always provide at least one recommendation
    if (recommendations.length === 0) {
      recommendations.push("Continue maintaining your current academic performance");
    }

    const newPrediction = {
      id: Date.now(),
      predicted_performance: student.previous_gpa >= 3.5 ? "High" : student.previous_gpa >= 2.5 ? "Medium" : "Low",
      risk_score: Math.max(0, Math.min(1, 1 - (student.previous_gpa / 4.0))),
      recommendations: JSON.stringify(recommendations),
      created_at: new Date().toISOString()
    };

    try {
      // Try to save to backend
      await axios.post(`http://localhost:8000/api/predictions/predict`, {
        student_id: student.id,
        features: {
          attendance_percentage: student.attendance_percentage,
          internal_marks: student.internal_marks,
          assignment_scores: student.assignment_scores,
          lab_performance: student.lab_performance,
          previous_gpa: student.previous_gpa,
          study_hours: student.study_hours,
          participation_metrics: student.participation_metrics
        }
      });
    } catch (backendError) {
      console.log('Backend prediction not available, saving locally:', backendError);
    }

    // Save to localStorage
    const updatedPredictions = [newPrediction, ...predictions];
    setPredictions(updatedPredictions);
    localStorage.setItem(`predictions_${user.email}`, JSON.stringify(updatedPredictions));
  };

  if (loading) return <div className="container mx-auto p-6"><div className="text-center">Loading...</div></div>;

  if (error) return <div className="container mx-auto p-6"><div className="text-center text-red-600">{error}</div></div>;

  if (!student) return <div className="container mx-auto p-6"><div className="text-center">Student not found</div></div>;

  const chartData = [
    { subject: 'Internal Marks', score: student.internal_marks || 0 },
    { subject: 'Assignment Scores', score: student.assignment_scores || 0 },
    { subject: 'Lab Performance', score: student.lab_performance || 0 },
  ];

  const getRiskColor = (riskScore) => {
    if (riskScore < 0.3) return 'text-green-600';
    if (riskScore < 0.7) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getPerformanceColor = (performance) => {
    switch (performance?.toLowerCase()) {
      case 'high': return 'text-green-600';
      case 'medium': return 'text-yellow-600';
      case 'low': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Student Profile</h1>
      
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <h2 className="text-xl font-semibold mb-4">Personal Information</h2>
        <div className="grid grid-cols-2 gap-4">
          <p><strong>Name:</strong> {student.name}</p>
          <p><strong>Email:</strong> {student.email}</p>
          <p><strong>Major:</strong> {student.major || 'Not specified'}</p>
          <p><strong>Enrollment Year:</strong> {student.enrollment_year || 'Not specified'}</p>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <h2 className="text-xl font-semibold mb-4">Academic Performance</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
          <div className="text-center">
            <p className="text-2xl font-bold text-blue-600">{student.attendance_percentage || 0}%</p>
            <p>Attendance</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-green-600">{student.previous_gpa || 0}</p>
            <p>Previous GPA</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-purple-600">{student.study_hours || 0}</p>
            <p>Study Hours/Week</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-orange-600">{student.participation_metrics || 0}%</p>
            <p>Participation</p>
          </div>
        </div>
        
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="subject" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="score" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Performance Predictions</h2>
          <button 
            onClick={generatePrediction}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Generate New Prediction
          </button>
        </div>
        
        {predictions.length > 0 ? (
          <div className="space-y-4">
            {predictions.map(pred => (
              <div key={pred.id} className="border p-4 rounded-lg">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <p className="font-semibold">Predicted Performance:</p>
                    <p className={`text-lg font-bold ${getPerformanceColor(pred.predicted_performance)}`}>
                      {pred.predicted_performance}
                    </p>
                  </div>
                  <div>
                    <p className="font-semibold">Risk Score:</p>
                    <p className={`text-lg font-bold ${getRiskColor(pred.risk_score)}`}>
                      {pred.risk_score?.toFixed(2) || 'N/A'}
                    </p>
                  </div>
                  <div>
                    <p className="font-semibold">Date:</p>
                    <p>{new Date(pred.created_at).toLocaleDateString()}</p>
                  </div>
                </div>
                <div className="mt-4">
                  <p className="font-semibold mb-2">Recommendations:</p>
                  <ul className="list-disc list-inside space-y-1">
                    {(() => {
                      try {
                        const recs = typeof pred.recommendations === 'string' 
                          ? JSON.parse(pred.recommendations) 
                          : pred.recommendations;
                        return Array.isArray(recs) 
                          ? recs.map((rec, index) => <li key={index}>{rec}</li>)
                          : <li>{recs}</li>;
                      } catch (e) {
                        return <li>{pred.recommendations}</li>;
                      }
                    })()}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-600 mb-4">No predictions available yet.</p>
            <p className="text-sm text-gray-500">Click "Generate New Prediction" to create your first performance prediction based on your academic data.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentProfile;
