import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();

  const [performanceData, setPerformanceData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalStudents: 0,
    atRiskStudents: 0,
    averageGpa: 0
  });

  /* ---------------- AI RISK CALCULATION ---------------- */
  const calculateRiskFromPredictions = () => {
    let atRiskCount = 0;
    let total = 0;
    let totalGpa = 0;
    let gpaCount = 0;

    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);

      // Read prediction data
      if (key && key.startsWith('predictions_')) {
        const predictions = JSON.parse(localStorage.getItem(key));

        if (Array.isArray(predictions) && predictions.length > 0) {
          const riskScore = Number(predictions[0].risk_score);

          if (!isNaN(riskScore)) {
            total++;
            if (riskScore >= 0.6) {
              atRiskCount++;
            }
          }
        }
      }

      // Read academic data for GPA average
      if (key && key.startsWith('academic_')) {
        const academic = JSON.parse(localStorage.getItem(key));
        const gpa = Number(academic?.previous_gpa);
        if (!isNaN(gpa)) {
          totalGpa += gpa;
          gpaCount++;
        }
      }
    }

    return {
      totalStudents: total,
      atRiskStudents: atRiskCount,
      averageGpa: gpaCount > 0 ? (totalGpa / gpaCount).toFixed(2) : 0
    };
  };

  /* ---------------- PERFORMANCE TREND ---------------- */
  const generatePerformanceTrends = (currentUser) => {
    if (!currentUser) return [];

    const academicData = localStorage.getItem(
      `academic_${currentUser.email.toLowerCase()}`
    );

    if (!academicData) return [];

    const data = JSON.parse(academicData);
    const baseGpa = data.previous_gpa || 3.0;
    const attendance = data.attendance_percentage || 85;

    const semesters = [
      'Fall 2022',
      'Spring 2023',
      'Fall 2023',
      'Spring 2024',
      'Fall 2024',
      'Spring 2025'
    ];

    return semesters.map((semester, index) => {
      const gpa = Math.min(
        4,
        Math.max(2, baseGpa - 0.3 + index * 0.1 + (Math.random() - 0.5) * 0.2)
      );

      const attend = Math.min(
        100,
        Math.max(70, attendance + (Math.random() - 0.5) * 10)
      );

      return {
        semester,
        average_gpa: gpa.toFixed(2),
        attendance_rate: Math.round(attend)
      };
    });
  };

  /* ---------------- LOAD DASHBOARD ---------------- */
  useEffect(() => {
    if (!user) {
      setLoading(false);
      return;
    }

    const loadDashboardData = async () => {
      try {
        // Backend call (optional – stats still AI-based)
        await axios.get('http://localhost:8002/api/students/');
      } catch (err) {
        console.log('Backend optional, using AI + localStorage');
      }

      // AI-based stats
      const aiStats = calculateRiskFromPredictions();
      setStats(aiStats);

      // User performance trend
      setPerformanceData(generatePerformanceTrends(user));

      setLoading(false);
    };

    loadDashboardData();
  }, [user]);

  /* ---------------- UI STATES ---------------- */
  if (loading) {
    return (
      <div className="container mx-auto p-6 text-center">
        Loading dashboard...
      </div>
    );
  }

  if (!user) {
    return (
      <div className="container mx-auto p-6 text-center">
        <h1 className="text-3xl font-bold mb-4">
          Student Performance System
        </h1>
        <p className="text-gray-600">
          Please login to view dashboard insights
        </p>
      </div>
    );
  }

  /* ---------------- JSX ---------------- */
  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">
        AI Performance Dashboard
      </h1>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard
          title="Total Students (Predicted)"
          value={stats.totalStudents}
          color="text-blue-600"
        />
        <StatCard
          title="At-Risk Students (AI)"
          value={stats.atRiskStudents}
          color="text-red-600"
        />
        <StatCard
          title="Average GPA"
          value={stats.averageGpa}
          color="text-green-600"
        />
      </div>

      {/* Trends */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">
          Your Performance Trend
        </h2>

        {performanceData.length > 0 ? (
          <div className="space-y-2">
            {performanceData.map((item, index) => (
              <div key={index} className="border p-3 rounded">
                <strong>{item.semester}</strong> — GPA:
                <span className="text-blue-600 font-semibold">
                  {' '}{item.average_gpa}
                </span>
                , Attendance:
                <span className="text-green-600 font-semibold">
                  {' '}{item.attendance_rate}%
                </span>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">
            Add academic details to view trends
          </p>
        )}
      </div>
    </div>
  );
};

/* ---------------- SMALL COMPONENT ---------------- */
const StatCard = ({ title, value, color }) => (
  <div className="bg-white p-6 rounded-lg shadow-md">
    <h2 className="text-xl font-semibold mb-2">{title}</h2>
    <p className={`text-3xl font-bold ${color}`}>{value}</p>
  </div>
);

export default Dashboard;
