import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer,
  PieChart, Pie, Cell
} from 'recharts';

const Analytics = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [students, setStudents] = useState([]);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // ================= FETCH DATA =================
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        const studentsRes = await axios.get('http://localhost:8004/api/students/');
        const predictionsRes = await axios.get('http://localhost:8004/api/predictions/');

        setStudents(studentsRes.data || []);
        setPredictions(predictionsRes.data || []);
      } catch (err) {
        console.error(err);
        setError('Failed to load analytics data');
      } finally {
        setLoading(false);
      }
    };

    if (user?.role === 'admin') {
      fetchData();
    } else {
      setLoading(false);
    }
  }, [user]);

  // ================= METRICS (HOOK ALWAYS RUNS) =================
  const metrics = useMemo(() => {
    const studentsWithPredictionsArr = students.filter(s =>
      predictions.some(p => p.student_id === s._id)
    );

    const riskLevels = { low: 0, medium: 0, high: 0 };
    const performanceLevels = { high: 0, medium: 0, low: 0 };

    studentsWithPredictionsArr.forEach(student => {
      const p = predictions.find(x => x.student_id === student._id);
      if (!p) return;

      if (p.risk_score >= 0.7) riskLevels.high++;
      else if (p.risk_score >= 0.3) riskLevels.medium++;
      else riskLevels.low++;

      const perf = p.predicted_performance?.toLowerCase();
      if (perf === 'high') performanceLevels.high++;
      else if (perf === 'medium') performanceLevels.medium++;
      else if (perf === 'low') performanceLevels.low++;
    });

    const avg = (key) =>
      students.reduce((sum, s) => sum + (s[key] || 0), 0) / (students.length || 1);

    return {
      totalStudents: students.length,
      studentsWithPredictions: studentsWithPredictionsArr.length,
      riskLevels,
      performanceLevels,
      averageGpa: avg('previous_gpa'),
      averageAttendance: avg('attendance_percentage'),
      averageStudyHours: avg('study_hours')
    };
  }, [students, predictions]);

  // ================= CONDITIONAL RENDERING (AFTER HOOKS) =================
  if (!user || user.role !== 'admin') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-red-600 mb-4">Access Denied</h1>
          <p className="text-gray-600">Admin privileges required</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="animate-spin h-12 w-12 border-b-2 border-blue-600 rounded-full" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <h1 className="text-red-600 text-xl font-bold">{error}</h1>
      </div>
    );
  }

  // ================= UI =================
  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-4xl font-bold mb-6">Analytics Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <Metric title="Total Students" value={metrics.totalStudents} />
        <Metric title="With Predictions" value={metrics.studentsWithPredictions} />
        <Metric title="Avg GPA" value={metrics.averageGpa.toFixed(2)} />
        <Metric title="Avg Attendance" value={`${metrics.averageAttendance.toFixed(1)}%`} />
      </div>

      <div className="bg-white rounded-xl shadow p-6">
        <h2 className="text-xl font-semibold mb-4">Student GPA & Attendance</h2>
        <ResponsiveContainer width="100%" height={350}>
          <BarChart data={students.slice(0, 10)}>
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="previous_gpa" fill="#6366F1" />
            <Bar dataKey="attendance_percentage" fill="#22C55E" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

// ================= REUSABLE =================
const Metric = ({ title, value }) => (
  <div className="bg-white rounded-xl shadow p-6">
    <h3 className="text-sm text-gray-500">{title}</h3>
    <p className="text-3xl font-bold mt-2">{value}</p>
  </div>
);

export default Analytics;
