import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const ChatBot = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Hello! I'm your AI assistant. I can help you with questions about student performance, risk analysis, and academic insights. What would you like to know?",
      sender: 'bot',
      timestamp: new Date(),
      confidence: 1.0
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId] = useState(`session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  const [showExplanation, setShowExplanation] = useState({});
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const generateSessionId = () => {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  };

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;
    
    console.log('ChatBot Debug - User object:', user);
    console.log('ChatBot Debug - User email:', user?.email);
    console.log('ChatBot Debug - User logged in:', !!user);
    console.log('ChatBot Debug - Input message:', inputMessage);

    // Check if user is authenticated
    if (!user || !user.email) {
      console.error('ChatBot Error - User not authenticated');
      const errorMessage = {
        id: Date.now() + 1,
        text: "Please log in to use the chatbot. User authentication is required.",
        sender: 'bot',
        timestamp: new Date(),
        confidence: 0.0,
        error: true
      };
      setMessages(prev => [...prev, errorMessage]);
      return;
    }

    const userMessage = {
      id: Date.now(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await axios.post(
        'http://localhost:8003/api/chatbot/chat',
        {
          message: inputMessage,
          session_id: sessionId,
          user_email: user.email
        },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('ChatBot Debug - API Response:', response.data);

      const botResponse = {
        id: Date.now() + 1,
        text: response.data.response,
        sender: 'bot',
        timestamp: new Date(),
        confidence: response.data.confidence,
        dataSources: response.data.data_sources,
        followUpQuestions: response.data.follow_up_questions,
        explanation: response.data.explanation
      };

      setMessages(prev => [...prev, botResponse]);
    } catch (error) {
      console.error('Error sending message:', error);
      console.log('ChatBot Debug - Error details:', error.response?.data);
      
      const errorMessage = {
        id: Date.now() + 1,
        text: "Sorry, I encountered an error processing your request. Please try again later.",
        sender: 'bot',
        timestamp: new Date(),
        confidence: 0.0,
        error: true
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const toggleExplanation = (messageId) => {
    setShowExplanation(prev => ({
      ...prev,
      [messageId]: !prev[messageId]
    }));
  };

  const askFollowUpQuestion = (question) => {
    setInputMessage(question);
    inputRef.current?.focus();
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 0.9) return 'text-green-600';
    if (confidence >= 0.7) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getConfidenceLabel = (confidence) => {
    if (confidence >= 0.9) return 'High Confidence';
    if (confidence >= 0.7) return 'Medium Confidence';
    return 'Low Confidence';
  };

  const formatTimestamp = (timestamp) => {
    return timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-lg">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 rounded-t-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
            <h3 className="font-semibold text-lg">AI Assistant</h3>
          </div>
          <div className="flex items-center space-x-2">
            <span className="text-xs bg-white bg-opacity-20 px-2 py-1 rounded">
              {user?.role === 'admin' ? 'Admin Access' : 'Student Access'}
            </span>
            {!user && (
              <span className="text-xs bg-red-500 px-2 py-1 rounded">
                Not Logged In
              </span>
            )}
          </div>
        </div>
        <p className="text-sm text-blue-100 mt-1">
          Ask me about student performance, risk analysis, or academic insights
        </p>
      </div>

      {/* Messages Container */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-3 rounded-lg ${
                message.sender === 'user'
                  ? 'bg-blue-600 text-white'
                  : message.error
                  ? 'bg-red-100 text-red-800 border border-red-200'
                  : 'bg-white border border-gray-200 text-gray-800'
              }`}
            >
              {/* Message Text */}
              <p className="text-sm whitespace-pre-wrap">{message.text}</p>

              {/* Bot Message Metadata */}
              {message.sender === 'bot' && !message.error && (
                <div className="mt-3 space-y-2">
                  {/* Confidence Score */}
                  <div className="flex items-center justify-between">
                    <span className={`text-xs ${getConfidenceColor(message.confidence)}`}>
                      {getConfidenceLabel(message.confidence)}
                    </span>
                    <span className="text-xs text-gray-400">
                      {(message.confidence * 100).toFixed(0)}%
                    </span>
                  </div>

                  {/* Data Sources */}
                  {message.dataSources && message.dataSources.length > 0 && (
                    <div className="text-xs text-gray-500">
                      <span className="font-medium">Sources:</span> {message.dataSources.join(', ')}
                    </div>
                  )}

                  {/* Explanation Toggle */}
                  {message.explanation && (
                    <div>
                      <button
                        onClick={() => toggleExplanation(message.id)}
                        className="text-xs text-blue-600 hover:text-blue-800 underline"
                      >
                        {showExplanation[message.id] ? 'Hide' : 'Show'} Explanation
                      </button>
                      {showExplanation[message.id] && (
                        <div className="mt-2 p-2 bg-blue-50 rounded text-xs text-gray-700">
                          {message.explanation}
                        </div>
                      )}
                    </div>
                  )}

                  {/* Follow-up Questions */}
                  {message.followUpQuestions && message.followUpQuestions.length > 0 && (
                    <div className="mt-2">
                      <p className="text-xs font-medium text-gray-600 mb-1">Suggested questions:</p>
                      <div className="space-y-1">
                        {message.followUpQuestions.map((question, index) => (
                          <button
                            key={index}
                            onClick={() => askFollowUpQuestion(question)}
                            className="block w-full text-left text-xs bg-gray-100 hover:bg-gray-200 p-2 rounded transition-colors"
                          >
                            üí¨ {question}
                          </button>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Timestamp */}
              <div className={`mt-1 text-xs ${
                message.sender === 'user' ? 'text-blue-100' : 'text-gray-400'
              }`}>
                {formatTimestamp(message.timestamp)}
              </div>
            </div>
          </div>
        ))}

        {/* Loading Indicator */}
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white border border-gray-200 rounded-lg px-4 py-3">
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <span className="text-sm text-gray-600">Thinking...</span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="border-t border-gray-200 p-4 bg-white rounded-b-lg">
        <div className="flex items-end space-x-2">
          <div className="flex-1">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask about student performance, risk analysis, or academic insights..."
              className="w-full px-4 py-3 border border-gray-300 rounded-lg resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              rows={2}
              disabled={isLoading}
            />
          </div>
          <button
            onClick={sendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {isLoading ? (
              <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
            ) : (
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
              </svg>
            )}
          </button>
        </div>

        {/* Quick Actions */}
        <div className="mt-3 flex flex-wrap gap-2">
          <button
            onClick={() => setInputMessage("How many students are at risk and why?")}
            className="text-xs bg-gray-100 hover:bg-gray-200 px-3 py-1 rounded-full transition-colors"
          >
            üìä Risk Analysis
          </button>
          <button
            onClick={() => setInputMessage("What is my current performance status?")}
            className="text-xs bg-gray-100 hover:bg-gray-200 px-3 py-1 rounded-full transition-colors"
          >
            üìà My Performance
          </button>
          <button
            onClick={() => setInputMessage("Compare class performance by major")}
            className="text-xs bg-gray-100 hover:bg-gray-200 px-3 py-1 rounded-full transition-colors"
          >
            üìö Class Comparison
          </button>
          <button
            onClick={() => setInputMessage("What factors affect student success?")}
            className="text-xs bg-gray-100 hover:bg-gray-200 px-3 py-1 rounded-full transition-colors"
          >
            üîç Success Factors
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
