import React, { useEffect, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const StudentProfile = () => {
  const { user } = useAuth();

  const [student, setStudent] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  /* -------------------- LOAD STUDENT DATA -------------------- */
  useEffect(() => {
    if (!user) {
      setError('Please login to view your profile');
      setLoading(false);
      return;
    }

    const loadStudent = async () => {
      try {
        // 1ï¸âƒ£ Try localStorage first
        const localData = localStorage.getItem(`academic_${user.email.toLowerCase()}`);
        if (localData) {
          const parsed = JSON.parse(localData);
          setStudent({
            id: user.id,
            name: user.name,
            email: user.email,
            ...parsed
          });
          setLoading(false);
          return;
        }

        // 2ï¸âƒ£ Try backend
        const res = await axios.get(`http://localhost:8002/api/students/by-user/${user.id}`);
        setStudent(res.data);
      } catch (err) {
        setError('Please add your academic details first');
      } finally {
        setLoading(false);
      }
    };

    loadStudent();
  }, [user]);

  /* -------------------- PREDICTION LOGIC -------------------- */
  const generatePrediction = async () => {
    if (!student) return;

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:8001/predict', {
        student_id: student.id,
        attendance_percentage: student.attendance_percentage,
        internal_marks: student.internal_marks,
        assignment_scores: student.assignment_scores,
        lab_performance: student.lab_performance,
        previous_gpa: student.previous_gpa,
        study_hours: student.study_hours,
        participation_metrics: student.participation_metrics
      });

      const newPrediction = {
        id: Date.now(),
        predicted_performance: response.data.predicted_performance,
        risk_score: response.data.risk_score,
        recommendations: response.data.recommendations,
        created_at: new Date().toISOString()
      };

      setPredictions([newPrediction]);
      localStorage.setItem(
        `predictions_${user.email.toLowerCase()}`,
        JSON.stringify([newPrediction])
      );
    } catch (err) {
      // ðŸ” Fallback logic
      const fallback = generateFallbackPrediction(student);
      setPredictions([fallback]);
    } finally {
      setLoading(false);
    }
  };

  /* -------------------- FALLBACK ANALYSIS -------------------- */
  const generateFallbackPrediction = (student) => {
    let risk = 0;

    if (student.attendance_percentage < 75) risk += 0.3;
    if (student.study_hours < 15) risk += 0.3;
    if (student.previous_gpa < 2.5) risk += 0.4;

    const performance =
      risk < 0.3 ? 'High' : risk < 0.6 ? 'Medium' : 'Low';

    return {
      id: Date.now(),
      predicted_performance: performance,
      risk_score: Math.min(1, risk),
      recommendations: [
        'Improve attendance',
        'Increase study hours',
        'Focus on weak subjects'
      ],
      created_at: new Date().toISOString()
    };
  };

  /* -------------------- UI STATES -------------------- */
  if (loading) return <div className="p-6 text-center">Loading...</div>;
  if (error) return <div className="p-6 text-center text-red-600">{error}</div>;
  if (!student) return <div className="p-6 text-center">No student found</div>;

  /* -------------------- JSX -------------------- */
  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Student Profile</h1>

      {/* Personal Info */}
      <div className="bg-white p-6 rounded shadow mb-6">
        <h2 className="text-xl font-semibold mb-4">Personal Info</h2>
        <p><b>Name:</b> {student.name}</p>
        <p><b>Email:</b> {student.email}</p>
      </div>

      {/* Academic Info */}
      <div className="bg-white p-6 rounded shadow mb-6">
        <h2 className="text-xl font-semibold mb-4">Academic Performance</h2>
        <p>Attendance: {student.attendance_percentage}%</p>
        <p>Previous GPA: {student.previous_gpa}</p>
        <p>Study Hours / Week: {student.study_hours}</p>
      </div>

      {/* Prediction */}
      <div className="bg-white p-6 rounded shadow">
        <div className="flex justify-between mb-4">
          <h2 className="text-xl font-semibold">Prediction</h2>
          {predictions.length === 0 && (
            <button
              onClick={generatePrediction}
              className="bg-blue-600 text-white px-4 py-2 rounded"
            >
              Generate Prediction
            </button>
          )}
        </div>

        {predictions.length > 0 && predictions.map(pred => (
          <div key={pred.id} className="border p-4 rounded">
            <p><b>Performance:</b> {pred.predicted_performance}</p>
            <p><b>Risk Score:</b> {pred.risk_score.toFixed(2)}</p>
            <ul className="list-disc ml-6 mt-2">
              {pred.recommendations.map((rec, i) => (
                <li key={i}>{rec}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StudentProfile;
