import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for stored user data on mount
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const generateUserId = (email) => {
    // Create consistent ID based on email hash (case insensitive)
    const lowerEmail = email.toLowerCase();
    return Math.abs(lowerEmail.split('').reduce((a, b) => {
      a = ((a << 5) - a) + b.charCodeAt(0);
      return a & a;
    }, 0)) % 10000 + 1;
  };

  const hashPassword = (password) => {
    // Simple hash function for demo (in production, use bcrypt)
    let hash = 0;
    for (let i = 0; i < password.length; i++) {
      const char = password.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32bit integer
    }
    return hash.toString();
  };

  const getUsers = () => {
    const storedUsers = localStorage.getItem('registeredUsers');
    return storedUsers ? JSON.parse(storedUsers) : {};
  };

  const saveUsers = (users) => {
    localStorage.setItem('registeredUsers', JSON.stringify(users));
  };

  const login = async (email, password, name = null) => {
    try {
      if (!email || !password) {
        return { success: false, error: 'Email and password are required' };
      }

      const users = getUsers();
      const lowerEmail = email.toLowerCase();
      
      // Check if user exists and password matches
      if (users[lowerEmail]) {
        const storedUser = users[lowerEmail];
        const hashedPassword = hashPassword(password);
        
        if (storedUser.password === hashedPassword) {
          const userData = {
            id: storedUser.id,
            name: storedUser.name,
            email: storedUser.email,
            role: storedUser.role
          };
          
          setUser(userData);
          localStorage.setItem('user', JSON.stringify(userData));
          return { success: true, user: userData };
        } else {
          return { success: false, error: 'Invalid password' };
        }
      } else {
        return { success: false, error: 'User not found. Please sign up first.' };
      }
    } catch (error) {
      return { success: false, error: error.message };
    }
  };

  const signup = async (name, email, password) => {
    try {
      if (!name || !email || !password) {
        return { success: false, error: 'All fields are required' };
      }

      if (password.length < 6) {
        return { success: false, error: 'Password must be at least 6 characters long' };
      }

      const users = getUsers();
      const lowerEmail = email.toLowerCase();
      
      // Check if user already exists
      if (users[lowerEmail]) {
        return { success: false, error: 'User with this email already exists' };
      }

      // Create new user
      const hashedPassword = hashPassword(password);
      const newUserId = generateUserId(email);
      
      const newUser = {
        id: newUserId,
        name: name,
        email: email,
        password: hashedPassword,
        role: email.includes('admin') ? 'admin' : 'student',
        createdAt: new Date().toISOString()
      };
      
      // Save user to storage
      users[lowerEmail] = newUser;
      saveUsers(users);
      
      // Auto-login after signup
      const userData = {
        id: newUserId,
        name: name,
        email: email,
        role: newUser.role
      };
      
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      return { success: true, user: userData };
    } catch (error) {
      return { success: false, error: error.message };
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const value = {
    user,
    login,
    signup,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
