import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();
  const [performanceData, setPerformanceData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [allUsers, setAllUsers] = useState([]);
  const [stats, setStats] = useState({
    totalStudents: 0,
    atRiskStudents: 0,
    averageGpa: 0
  });

  useEffect(() => {
    if (!user) {
      setLoading(false);
      return;
    }

    const loadDashboardData = async () => {
      try {
        // Load all users' academic data for dashboard stats
        const usersResponse = await axios.get('http://localhost:8002/api/students/');
        const users = usersResponse.data;
        setAllUsers(users);

        // Load all predictions for risk assessment
        const predictionsData = await loadAllPredictions(users);
        
        // Calculate statistics based on prediction risk scores
        const totalStudents = users.length;
        const averageGpa = users.reduce((sum, u) => sum + (u.previous_gpa || 0), 0) / totalStudents || 0;
        const atRiskStudents = predictionsData.filter(p => p.risk_score >= 0.5).length;

        setStats({
          totalStudents,
          atRiskStudents,
          averageGpa: averageGpa.toFixed(2)
        });

        // Generate performance trends from real data
        const trends = generatePerformanceTrends(user, predictionsData);
        setPerformanceData(trends);
        
      } catch (error) {
        console.log('Backend not available, using localStorage data:', error);
        
        // Fallback to localStorage data
        const trends = generatePerformanceTrends(user);
        setPerformanceData(trends);
        
        // Calculate stats from localStorage with risk assessment
        const allAcademicData = [];
        const allPredictions = [];
        
        for (let i = 0; i < localStorage.length; i++) {
          const key = localStorage.key(i);
          if (key && key.startsWith('academic_')) {
            const data = JSON.parse(localStorage.getItem(key));
            allAcademicData.push(data);
          }
          if (key && key.startsWith('predictions_')) {
            const predictions = JSON.parse(localStorage.getItem(key));
            allPredictions.push(...predictions);
          }
        }
        
        const totalStudents = allAcademicData.length;
        const averageGpa = allAcademicData.reduce((sum, u) => sum + (u.previous_gpa || 0), 0) / totalStudents || 0;
        const atRiskStudents = allPredictions.filter(p => p.risk_score >= 0.5).length;

        setStats({
          totalStudents,
          atRiskStudents,
          averageGpa: averageGpa.toFixed(2)
        });
      }
      
      setLoading(false);
    };

    loadDashboardData();
  }, [user]);

  // Load predictions for all students to assess risk
  const loadAllPredictions = async (users) => {
    const allPredictions = [];
    
    for (const user of users) {
      try {
        // Try to get predictions for each user
        const userEmail = user.email;
        const storedPredictions = localStorage.getItem(`predictions_${userEmail.toLowerCase()}`);
        
        if (storedPredictions) {
          const predictions = JSON.parse(storedPredictions);
          if (predictions.length > 0) {
            allPredictions.push({
              studentId: user.id || user._id,
              studentName: user.name,
              studentEmail: userEmail,
              prediction: predictions[0] // Get latest prediction
            });
          }
        }
      } catch (e) {
        console.log('Error loading predictions for user:', user.email, e);
      }
    }
    
    return allPredictions;
  };

  const generatePerformanceTrends = (currentUser) => {
    if (!currentUser) return [];

    // Get user's academic data
    const academicData = localStorage.getItem(`academic_${currentUser.id}`);
    if (!academicData) return [];

    const data = JSON.parse(academicData);
    const currentGpa = data.previous_gpa || 3.0;
    const attendance = data.attendance_percentage || 85;

    // Generate realistic performance trends based on current data
    const semesters = [
      'Fall 2022', 'Spring 2023', 'Fall 2023', 
      'Spring 2024', 'Fall 2024', 'Spring 2025'
    ];

    return semesters.map((semester, index) => {
      // Simulate progression with some variation
      const baseGpa = currentGpa - 0.3 + (index * 0.1);
      const variation = (Math.random() - 0.5) * 0.2;
      const semesterGpa = Math.max(2.0, Math.min(4.0, baseGpa + variation));
      const semesterAttendance = Math.max(70, Math.min(100, attendance + (Math.random() - 0.5) * 10));

      return {
        semester,
        average_gpa: parseFloat(semesterGpa.toFixed(2)),
        attendance_rate: Math.round(semesterAttendance)
      };
    });
  };

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold mb-6">Student Performance System</h1>
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold mb-6">Student Performance System</h1>
        
        <div className="bg-white p-8 rounded-lg shadow-md mb-8">
          <div className="text-center mb-8">
            <h2 className="text-2xl font-bold mb-4">Welcome to Your Academic Success Platform</h2>
            <p className="text-gray-600 mb-6">Track performance, get predictions, and improve your academic journey</p>
            <div className="flex justify-center space-x-4">
              <button
                onClick={() => window.location.href = '#login'}
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700"
              >
                Login
              </button>
              <button
                onClick={() => window.location.href = '#signup'}
                className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700"
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="text-4xl mb-4">ðŸ“Š</div>
            <h3 className="text-xl font-semibold mb-2">Performance Tracking</h3>
            <p className="text-gray-600">Monitor your academic progress with detailed analytics</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="text-4xl mb-4">ðŸŽ¯</div>
            <h3 className="text-xl font-semibold mb-2">AI Predictions</h3>
            <p className="text-gray-600">Get personalized performance predictions</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md text-center">
            <div className="text-4xl mb-4">ðŸ’¡</div>
            <h3 className="text-xl font-semibold mb-2">Smart Recommendations</h3>
            <p className="text-gray-600">Receive actionable improvement suggestions</p>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">How It Works</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="bg-blue-100 w-12 h-12 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-blue-600 font-bold">1</span>
              </div>
              <h4 className="font-semibold">Sign Up</h4>
              <p className="text-sm text-gray-600">Create your account</p>
            </div>
            <div className="text-center">
              <div className="bg-blue-100 w-12 h-12 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-blue-600 font-bold">2</span>
              </div>
              <h4 className="font-semibold">Add Details</h4>
              <p className="text-sm text-gray-600">Input academic information</p>
            </div>
            <div className="text-center">
              <div className="bg-blue-100 w-12 h-12 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-blue-600 font-bold">3</span>
              </div>
              <h4 className="font-semibold">Get Analysis</h4>
              <p className="text-sm text-gray-600">View performance insights</p>
            </div>
            <div className="text-center">
              <div className="bg-blue-100 w-12 h-12 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-blue-600 font-bold">4</span>
              </div>
              <h4 className="font-semibold">Improve</h4>
              <p className="text-sm text-gray-600">Follow recommendations</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Student Performance Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-2">Total Students</h2>
          <p className="text-3xl font-bold text-blue-600">{stats.totalStudents}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-2">At-Risk Students</h2>
          <p className="text-3xl font-bold text-red-600">{stats.atRiskStudents}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-2">Average GPA</h2>
          <p className="text-3xl font-bold text-green-600">{stats.averageGpa}</p>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Your Performance Trends</h2>
        {performanceData.length > 0 ? (
          <div className="space-y-2">
            {performanceData.map((data, index) => (
              <div key={index} className="border p-3 rounded">
                <p><strong>{data.semester}:</strong> GPA {data.average_gpa}, Attendance {data.attendance_rate}%</p>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-600">
            <p className="mb-4">No performance data available.</p>
            <p className="text-sm">Please add your academic details to see performance trends.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
