import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [allStudents, setAllStudents] = useState([]);
  const [allPredictions, setAllPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [stats, setStats] = useState({
    totalStudents: 0,
    atRiskStudents: 0,
    averageGpa: 0,
    highPerformers: 0,
    lowAttendance: 0
  });

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      return (
        <div className="container mx-auto p-6">
          <div className="text-center">
            <h1 className="text-3xl font-bold text-red-600 mb-4">Access Denied</h1>
            <p className="text-gray-600">You need admin privileges to access this page.</p>
          </div>
        </div>
      );
    }

    const loadAdminData = async () => {
      try {
        // Load all students
        const studentsResponse = await axios.get('http://localhost:8002/api/students/');
        const students = studentsResponse.data;
        setAllStudents(students);

        // Load all predictions
        const predictionsData = await loadAllPredictions(students);
        setAllPredictions(predictionsData);

        // Calculate comprehensive statistics
        const totalStudents = students.length;
        const averageGpa = students.reduce((sum, s) => sum + (s.previous_gpa || 0), 0) / totalStudents || 0;
        const atRiskStudents = predictionsData.filter(p => p.risk_score >= 0.5).length;
        const highPerformers = students.filter(s => (s.previous_gpa || 0) >= 3.5).length;
        const lowAttendance = students.filter(s => (s.attendance_percentage || 0) < 75).length;

        setStats({
          totalStudents,
          atRiskStudents,
          averageGpa: averageGpa.toFixed(2),
          highPerformers,
          lowAttendance
        });

      } catch (error) {
        console.error('Error loading admin data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadAdminData();
  }, [user]);

  // Load predictions for all students
  const loadAllPredictions = async (students) => {
    const allPredictions = [];
    
    for (const student of students) {
      try {
        const userEmail = student.email;
        const storedPredictions = localStorage.getItem(`predictions_${userEmail.toLowerCase()}`);
        
        if (storedPredictions) {
          const predictions = JSON.parse(storedPredictions);
          if (predictions.length > 0) {
            allPredictions.push({
              studentId: student.id || student._id,
              studentName: student.name,
              studentEmail: userEmail,
              student: student,
              prediction: predictions[0] // Get latest prediction
            });
          }
        }
      } catch (e) {
        console.log('Error loading predictions for student:', student.email, e);
      }
    }
    
    return allPredictions;
  };

  const getRiskLevel = (riskScore) => {
    if (riskScore >= 0.7) return { level: 'Critical', color: 'text-red-600', bg: 'bg-red-100' };
    if (riskScore >= 0.5) return { level: 'High', color: 'text-orange-600', bg: 'bg-orange-100' };
    if (riskScore >= 0.3) return { level: 'Medium', color: 'text-yellow-600', bg: 'bg-yellow-100' };
    return { level: 'Low', color: 'text-green-600', bg: 'bg-green-100' };
  };

  const getPerformanceLevel = (gpa) => {
    if (gpa >= 3.5) return { level: 'Excellent', color: 'text-green-600' };
    if (gpa >= 3.0) return { level: 'Good', color: 'text-blue-600' };
    if (gpa >= 2.5) return { level: 'Average', color: 'text-yellow-600' };
    return { level: 'Poor', color: 'text-red-600' };
  };

  const handleViewDetails = (student) => {
    setSelectedStudent(student);
    setShowDetailsModal(true);
  };

  const closeDetailsModal = () => {
    setSelectedStudent(null);
    setShowDetailsModal(false);
  };

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center space-x-4">
          <div className="text-center">
            <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>
            <div className="text-xl">Loading admin data...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-7xl mx-auto px-4">
        
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-gray-800">Admin Dashboard</h1>
          <p className="text-gray-500">Comprehensive student performance management</p>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Total Students</h3>
            <p className="text-3xl font-bold text-blue-600">{stats.totalStudents}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">At-Risk Students</h3>
            <p className="text-3xl font-bold text-red-600">{stats.atRiskStudents}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Average GPA</h3>
            <p className="text-3xl font-bold text-green-600">{stats.averageGpa}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">High Performers</h3>
            <p className="text-3xl font-bold text-purple-600">{stats.highPerformers}</p>
          </div>
          
          <div className="bg-white rounded-xl shadow p-6">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Low Attendance</h3>
            <p className="text-3xl font-bold text-orange-600">{stats.lowAttendance}</p>
          </div>
        </div>

        {/* Students Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-800">All Students</h2>
            <p className="text-sm text-gray-500 mt-1">Complete overview of all student performance and risk levels</p>
          </div>
          
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Major</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">GPA</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Attendance</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Risk Level</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Performance</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {allStudents.map((student, index) => {
                  const prediction = allPredictions.find(p => p.studentId === (student.id || student._id));
                  const riskLevel = prediction ? getRiskLevel(prediction.prediction.risk_score) : { level: 'No Data', color: 'text-gray-600', bg: 'bg-gray-100' };
                  const performanceLevel = getPerformanceLevel(student.previous_gpa || 0);
                  
                  return (
                    <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="h-10 w-10 flex-shrink-0">
                            <div className="h-10 w-10 rounded-full bg-blue-500 flex items-center justify-center">
                              <span className="text-white font-medium text-sm">
                                {student.name.charAt(0).toUpperCase()}
                              </span>
                            </div>
                          </div>
                          <div className="ml-4">
                            <div className="text-sm font-medium text-gray-900">{student.name}</div>
                            <div className="text-sm text-gray-500">{student.major || 'Not specified'}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.email}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.major || 'Not specified'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${performanceLevel.color}`}>
                          {student.previous_gpa?.toFixed(2) || 'N/A'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.attendance_percentage || 0}%
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${riskLevel.color} ${riskLevel.bg}`}>
                          {riskLevel.level}
                        </span>
                        {prediction && (
                          <div className="text-xs text-gray-500 mt-1">
                            {(prediction.prediction.risk_score * 100).toFixed(1)}%
                          </div>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${performanceLevel.color}`}>
                          {performanceLevel.level}
                        </span>
                        <div className="text-xs text-gray-500 mt-1">
                          GPA: {student.previous_gpa?.toFixed(2) || 'N/A'}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button 
                          onClick={() => handleViewDetails(student)}
                          className="text-blue-600 hover:text-blue-900 mr-3"
                        >
                          View Details
                        </button>
                        <button className="text-red-600 hover:text-red-900">Send Alert</button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>

        {/* Risk Students Summary */}
        {allPredictions.filter(p => p.prediction.risk_score >= 0.5).length > 0 && (
          <div className="mt-8 bg-white rounded-xl shadow overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-red-800">High-Risk Students Alert</h2>
              <p className="text-sm text-red-600 mt-1">Students requiring immediate attention</p>
            </div>
            
            <div className="p-6 space-y-4">
              {allPredictions
                .filter(p => p.prediction.risk_score >= 0.5)
                .map((item, index) => (
                  <div key={index} className="border-l-4 border-red-500 bg-red-50 p-4 rounded">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="font-semibold text-red-800">{item.studentName}</h3>
                        <p className="text-sm text-gray-600">{item.studentEmail}</p>
                        <p className="text-sm">GPA: {item.student.previous_gpa?.toFixed(2) || 'N/A'}</p>
                        <p className="text-sm">Risk Score: {(item.prediction.risk_score * 100)?.toFixed(0)}%</p>
                      </div>
                      <div className="text-right space-x-2">
                        <button className="bg-red-600 text-white px-3 py-1 rounded text-sm hover:bg-red-700">
                          Contact Student
                        </button>
                        <button className="bg-blue-600 text-white px-3 py-1 rounded text-sm hover:bg-blue-700">
                          View Full Report
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        )}
      </div>

      {/* Student Details Modal */}
      {showDetailsModal && selectedStudent && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Student Details</h2>
                <button
                  onClick={closeDetailsModal}
                  className="text-gray-500 hover:text-gray-700 text-2xl"
                >
                  
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Basic Information */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold mb-4 text-gray-800">Basic Information</h3>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="font-medium text-gray-600">Name:</span>
                      <span className="text-gray-800">{selectedStudent.name}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-gray-600">Email:</span>
                      <span className="text-gray-800">{selectedStudent.email}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-gray-600">Major:</span>
                      <span className="text-gray-800">{selectedStudent.major || 'Not specified'}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-gray-600">Enrollment Year:</span>
                      <span className="text-gray-800">{selectedStudent.enrollment_year || 'Not specified'}</span>
                    </div>
                  </div>
                </div>

                {/* Academic Performance */}
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold mb-4 text-blue-800">Academic Performance</h3>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="font-medium text-blue-600">Previous GPA:</span>
                      <span className="text-blue-800 font-semibold">{selectedStudent.previous_gpa?.toFixed(2) || 'N/A'}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-blue-600">Attendance:</span>
                      <span className="text-blue-800">{selectedStudent.attendance_percentage || 0}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-blue-600">Internal Marks:</span>
                      <span className="text-blue-800">{selectedStudent.internal_marks || 'N/A'}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-blue-600">Assignment Scores:</span>
                      <span className="text-blue-800">{selectedStudent.assignment_scores || 'N/A'}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="font-medium text-blue-600">Lab Performance:</span>
                      <span className="text-blue-800">{selectedStudent.lab_performance || 'N/A'}</span>
                    </div>
                  </div>
                </div>

                {/* Risk Assessment */}
                <div className="bg-red-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold mb-4 text-red-800">Risk Assessment</h3>
                  <div className="space-y-3">
                    {(() => {
                      const prediction = allPredictions.find(p => p.studentId === (selectedStudent.id || selectedStudent._id));
                      if (prediction) {
                        const riskScore = prediction.prediction.risk_score;
                        const riskLevel = getRiskLevel(riskScore);
                        return (
                          <>
                            <div className="flex justify-between">
                              <span className="font-medium text-red-600">Risk Score:</span>
                              <span className={`font-semibold ${riskLevel.color}`}>
                                {(riskScore * 100).toFixed(1)}%
                              </span>
                            </div>
                            <div className="flex justify-between">
                              <span className="font-medium text-red-600">Risk Level:</span>
                              <span className={`font-semibold ${riskLevel.color}`}>{riskLevel.level}</span>
                            </div>
                            <div className="flex justify-between">
                              <span className="font-medium text-red-600">Study Hours:</span>
                              <span className="text-red-800">{selectedStudent.study_hours || 'N/A'}</span>
                            </div>
                            <div className="flex justify-between">
                              <span className="font-medium text-red-600">Participation:</span>
                              <span className="text-red-800">{selectedStudent.participation_metrics || 'N/A'}</span>
                            </div>
                          </>
                        );
                      } else {
                        return (
                          <div className="text-center text-gray-500">
                            No prediction data available
                          </div>
                        );
                      }
                    })()}
                  </div>
                </div>

                {/* Additional Information */}
                <div className="bg-green-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold mb-4 text-green-800">Additional Information</h3>
                  <div className="space-y-3">
                    <div>
                      <span className="font-medium text-green-600">Socio-Academic Factors:</span>
                      <div className="text-green-800 mt-1 text-sm">
                        {selectedStudent.socio_academic_factors ? 
                          JSON.stringify(JSON.parse(selectedStudent.socio_academic_factors), null, 2) : 
                          'Not available'
                        }
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-6 flex justify-end space-x-3">
                <button
                  onClick={closeDetailsModal}
                  className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
